mapper.select:

select rno, gno ,content, userid, name, likes, regDate, updateDate
	from 
	(  select 
	  rownum rn, rno, gno , content, r.userid, likes, regDate, updateDate, m.name
	   from tbl_game_reviews r
	   left outer join tbl_member m on r.userid = m.userid 
	   where gno = #{gno}
	   and rno >0
	   and rownum <= #{cri.pageNum} * #{cri.amount}
	 ) where rn > (#{cri.pageNum}-1) * #{cri.amount}
