mapper.select:

select rno, gno ,content, userid, likes, regDate, updateDate
	from 
	( select 
	  rownum rn, rno, gno , content, userid, likes, regDate, updateDate
	   from tbl_game_reviews
	   where gno = #{gno}
	   and rno >0
	   and rownum <= #{cri.pageNum} * #{cri.amount}
	 ) where rn > (#{cri.pageNum}-1) * #{cri.amount}
